import React, { Component }from 'react';
import { CardList } from './components/card-list/card-list-componet'
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      monster: [],
      searchFeild: ''
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json())
    .then(users => this.setState({ monster: users }))
  }

  render() {
    const { monster, searchFeild } = this.state;
    const filteredMonster = monster.filter(monster => 
      monster.name.toLowerCase().include(searchFeild.toLowerCase()))
    return (
      <div className="App">
        <input type="search" placeholder="search monster" onChange={e => this.setState({ searchFeild: e.target.value })} />
        <CardList monster={filteredMonster}/>
     </div>
    )
  }
}

export default App;
